import React, { useState } from 'react';
import { Container, Typography, Box, TextField, Button, Tabs, Tab, Paper, CircularProgress } from '@mui/material';
import axios from 'axios';

function App() {
  const [tab, setTab] = useState(0);
  const [file, setFile] = useState(null);
  const [jsonInput, setJsonInput] = useState('');
  const [emailInput, setEmailInput] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleTabChange = (event, newValue) => {
    setTab(newValue);
    setResult(null);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setResult(null);
  };

  const handleSubmit = async () => {
    setLoading(true);
    setResult(null);
    const formData = new FormData();
    if (tab === 0 && file) {
      formData.append('file', file);
    } else if (tab === 1 && jsonInput.trim()) {
      formData.append('json_body', jsonInput);
    } else if (tab === 2 && emailInput.trim()) {
      formData.append('email_body', emailInput);
    } else {
      alert('Please provide input');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:8000/intake/', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setResult(response.data);
    } catch (error) {
      let errorMsg = error.message;
      if (error.response) {
        errorMsg = `Status: ${error.response.status}, Data: ${JSON.stringify(error.response.data)}`;
      }
      setResult({ error: errorMsg });
    }
    setLoading(false);
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom align="center">
        Multi-Format Intake Agent
      </Typography>
      <Paper square>
        <Tabs value={tab} onChange={handleTabChange} indicatorColor="primary" textColor="primary" centered>
          <Tab label="Upload PDF" />
          <Tab label="Enter JSON" />
          <Tab label="Enter Email" />
        </Tabs>
      </Paper>
      <Box sx={{ mt: 3 }}>
        {tab === 0 && (
          <input type="file" accept="application/pdf" onChange={handleFileChange} />
        )}
        {tab === 1 && (
          <TextField
            label="JSON Input"
            multiline
            rows={8}
            fullWidth
            value={jsonInput}
            onChange={(e) => setJsonInput(e.target.value)}
            placeholder='{"id": "123", "type": "order", "attributes": {"item": "book"}}'
          />
        )}
        {tab === 2 && (
          <TextField
            label="Email Body"
            multiline
            rows={8}
            fullWidth
            value={emailInput}
            onChange={(e) => setEmailInput(e.target.value)}
            placeholder="From: John Doe <john@example.com>\nSubject: RFQ for office supplies\nUrgency: ASAP\nConversation-ID: 789\n\nPlease send a quote for 100 pens."
          />
        )}
        <Box sx={{ mt: 2 }}>
          <Button variant="contained" color="primary" onClick={handleSubmit} disabled={loading}>
            {loading ? <CircularProgress size={24} /> : 'Submit'}
          </Button>
        </Box>
      </Box>
      {result && (
        <Box sx={{ mt: 4, whiteSpace: 'pre-wrap', backgroundColor: '#f5f5f5', p: 2, borderRadius: 1 }}>
          <Typography variant="h6">Result:</Typography>
          <Typography component="pre">{JSON.stringify(result, null, 2)}</Typography>
        </Box>
      )}
    </Container>
  );
}

export default App;
